import React, { Fragment, useState } from 'react'
import PropTypes from 'prop-types'
import {connect} from 'react-redux'
import {addExp} from '../../actions/profile'
import {Link,withRouter} from 'react-router-dom'

const AddExp = ({addExp,history}) => {
    const [formData,setFormData]=useState({
        title:'',company:'',location:'',from:'',to:'',current:false,description:''
    })
    const [toDateDisabled,toggleDisbled]=useState(false)
    const {company,title,location,from,to,current,description}=formData
    const onChange = e =>
    setFormData({ ...formData, [e.target.name]: e.target.value });
    
    return (
        <Fragment>
        <h1 className="large text-primary">Add An Experience</h1>
        <p className="lead">
          <i className="fas fa-code-branch" /> Add any developer/programming
          positions that you have had in the past
        </p>
        <small>* = required field</small>
        <form
          className="form"
          onSubmit={e => {
            e.preventDefault();
            addExp(formData, history);
          }}
        >
          <div className="form-group">
            <input
              type="text"
              placeholder="* Job Title"
              name="title"
              value={title}
              onChange={onChange}
              required
            />
          </div>
          <div className="form-group">
            <input
              type="text"
              placeholder="* Company"
              name="company"
              value={company}
              onChange={onChange}
              required
            />
          </div>
          <div className="form-group">
            <input
              type="text"
              placeholder="Location"
              name="location"
              value={location}
              onChange={onChange}
            />
          </div>
          <div className="form-group">
            <h4>From Date</h4>
            <input type="date" name="from" value={from} onChange={onChange} />
          </div>
          <div className="form-group">
            <p>
              <input
                type="checkbox"
                name="current"
                checked={current}
                value={current}
                onChange={() => {
                  setFormData({ ...formData, current: !current });
                }}
              />{' '}
              Current Job
            </p>
          </div>
          <div className="form-group">
            <h4>To Date</h4>
            <input
              type="date"
              name="to"
              value={to}
              onChange={onChange}
              disabled={current}
            />
          </div>
          <div className="form-group">
            <textarea
              name="description"
              cols="30"
              rows="5"
              placeholder="Job Description"
              value={description}
              onChange={onChange}
            />
          </div>
          <input type="submit" className="btn btn-primary my-1" />
          <Link className="btn btn-light my-1" to="/dashboard">
            Go Back
          </Link>
        </form>
      </Fragment>
    )
}

AddExp.propTypes = {
addExp:PropTypes.func.isRequired,

}

export default connect(null,{addExp})(withRouter(AddExp))
